import { USER_CREATE, USER_LOGIN } from "../constants/userConstants";
import * as api from "../api/articles-api";

export const createUser = (user) => async (dispatch) => {
  try {
    const { data } = await api.createUser(user);
    dispatch({
      type: USER_CREATE,
      payload: data,
    });
  } catch (error) {
    console.log(error);
  }
};

