import React, { useEffect } from "react";
import { useDispatch } from "react-redux";
import { useParams } from "react-router-dom";

const ArticleScreen = () => {
  const dispatch = useDispatch();
  const { id } = useParams();
  useEffect(() => {
    dispatch(listArticlesDetails(id));
  }, [dispatch, id]);
  return <div>
      {id}
  </div>;
};

export default ArticleScreen;
