import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import SubArticles from "../components/subArticles";
import Article from "../components/article";
import { Row, Col } from "react-bootstrap";
import { listArticles } from "../actions/articles-action";
import { listSubArticles } from "../actions/subarticles-actions";

const HomeScreen = () => {
  const articleList = useSelector((state) => state.articlesList);
  const subarticlesList = useSelector((state) => state.subarticlesList);
  return (
    <div className="container">
      <div className="row center">
        {articlesList.map((art) => (
          <div>{art.title}</div>
        ))}
      </div>
    </div>
  );
};

export default HomeScreen;
