import React, { useState, useEffect } from "react";
import { useHistory, useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { createArticle, listArticlesDetails } from "../actions/articles-action";
import FormData from "form-data";

const ArticleEdit = () => {
  let history = useHistory();
  const dispatch = useDispatch();
  const { slug } = useParams();
  useEffect(() => {
    dispatch(listArticlesDetails(slug));
  }, [dispatch, slug]);
  const article = useSelector((state) => state.articleUpdate);
  // const [title, setTitle] = useState("");
  // const [description, setDescription] = useState("");
  // const [image, setImage] = useState("");
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    image: "",
  });
  const { title, description, image } = formData;

  useEffect(() => {
    if (article) setFormData(article);
  }, [article]);

  const submitHandler = (e) => {
    e.preventDefault();
    formData.append("title", title);
    formData.append("description", description);
    formData.append("image", image);
    console.log(formData.values());
    for (let pair of formData.entries()) {
      console.log(pair[0] + ", " + pair[1]);
    }
    dispatch(createArticle(formData));
    history.push("/");
  };

  return (
    <div className="card border-0 shadow">
      <div className="card-header">Add Contact</div>
      <div className="card-body">
        <form onSubmit={(e) => submitHandler(e)}>
          <div className="form-group">
            <input
              type="text"
              className="form-control"
              value={title}
              onChange={(e) =>
                setFormData({ ...formData, title: e.target.value })
              }
              placeholder="Enter the Dragon "
            />
          </div>
          <div className="form-group">
            <input
              type="text"
              className="form-control"
              value={description}
              onChange={(e) =>
                setFormData({ ...formData, description: e.target.value })
              }
              placeholder="Enter the Email "
            />
          </div>
          <div className="form-group">
            <input
              id="post-image"
              onChange={(e) =>
                setFormData({ ...formData, image: e.target.files[0] })
              }
              name="image"
              type="file"
            />
          </div>
          <button className="btn btn-danger" type="submit">
            Submit
          </button>
        </form>
      </div>
    </div>
  );
};

export default ArticleEdit;
