import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { listArticles } from "../actions/articles-action";
import { createSubArticle } from "../actions/subarticles-actions";
import LoadingBox from "../components/loading.box";
import MessageBox from "../components/mesage.box";

const SubArticleEdit = () => {
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(listArticles());
  }, [dispatch]);
  const { articles, loading, error } = useSelector(
    (state) => state?.articlesList
  );

  const [post, setPost] = useState({
    title: "",
    description: "",
    image: "",
    available_on: "",
    maintitle: { maintitle: [] },
  });
  const { title, description, image, available_on, maintitle } = post;

  const selectHandler = (e) => {
    const selected = [];
    let selectedOption = e.target.selectedOptions;

    for (let i = 0; i < selectedOption.length; i++) {
      selected.push(selectedOption[i].value);
    }
    console.log("aaaaaaaaaaaaaaaa", selected);
    const final = selected.map(Number);

    setPost({ ...maintitle, [e.target.name]: selected });
    console.log("!!!!!!!!!!", final);
  };
  const submitHandler = (e) => {
    e.preventDefault();
    let formData = new FormData();
    console.log("#############");
    formData.append("title", post.title);
    formData.append("image", post.image);
    formData.append("description", post.description);
    formData.append("available_on", post.available_on);
    formData.append("maintitle", post.maintitle.maintitle);
    for (let pair of formData.entries()) {
      console.log(pair[0] + ", " + pair[1]);
    }
    dispatch(createSubArticle(formData));
  };
  return (
    <div className="container">
      {loading ? (
        <LoadingBox />
      ) : error ? (
        <MessageBox variant="danger">{error}</MessageBox>
      ) : (
        <div className="row center">
          <div className="card border-0 shadow">
            <div className="card-header">Add Articles</div>
            <div className="card-body">
              <form onSubmit={(e) => submitHandler(e)}>
                <div className="form-group">
                  <input
                    type="text"
                    className="form-control"
                    value={title}
                    onChange={(e) =>
                      setPost({ ...post, title: e.target.value })
                    }
                    placeholder="Enter the Dragon "
                  />
                </div>
                <div className="form-group">
                  <input
                    id="image"
                    // onChange={(e) => setFile(e.target.files[0])}
                    onChange={(e) =>
                      setPost({ ...post, image: e.target.files[0] })
                    }
                    name="image"
                    type="file"
                  />
                  <img src={image} height="50px" />
                </div>
                <select
                  class="selectpicker"
                  name="maintitle"
                  data-live-search="true"
                  onChange={selectHandler}
                >
                  {articles?.map((art) => (
                    <option value={art?.id}>{art.title}</option>
                  ))}
                </select>
                <div className="form-group">
                  <input
                    type="text"
                    className="form-control"
                    value={description}
                    onChange={(e) =>
                      setPost({ ...post, description: e.target.value })
                    }
                    placeholder="description"
                  />
                </div>
                <div className="form-group">
                  <input
                    type="text"
                    className="form-control"
                    value={available_on}
                    onChange={(e) =>
                      setPost({ ...post, available_on: e.target.value })
                    }
                    // onChange={(e) => setDescription(e.target.value)}
                    placeholder="avaliable on"
                  />
                </div>

                <button className="btn btn-danger" type="submit">
                  Submit
                </button>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default SubArticleEdit;
