import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { listArticles } from "../actions/articles-action";
import { listSubArticles } from "../actions/subarticles-actions";
import Article from "../components/article";
import SubArticles from "../components/subArticles";

const HomeScreen = () => {
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(listArticles());
  }, [dispatch]);
  useEffect(() => {
    dispatch(listSubArticles());
  }, [dispatch]);
  const articleList = useSelector((state) => state.articlesList);
  const subarticlesList = useSelector((state) => state.subarticlesList);
  return (
    <div>
      <div>
        {articleList.map((article) => (
          <Article key={article.slug} article={article} />
        ))}
      </div>
      <div>
        {subarticlesList.map((subarticle) => (
          <SubArticles key={subarticle.slug} sub={subarticle} />
        ))}
      </div>
    </div>
  );
};

export default HomeScreen;
