import axios from "axios";

const baseUrl = "http://localhost:8000/api";
export const fetchArticles = () => axios.get(`${baseUrl}/maintitle`);
export const fetchArticlesDetails = (id) => axios.get(`${baseUrl}/subtitle/${id}`)
