import {
  ARTICLES_DETAILS,
  ARTICLES_LIST,
} from "../constants/articlesConstants";

export const articlesListReducer = (state = [], action) => {
  switch (action.type) {
    case ARTICLES_LIST:
      return action.payload;
    case ARTICLES_DETAILS:
      return action.payload;
    default:
      return state;
  }
};
