import {
  ARTICLES_DETAILS,
  ARTICLES_LIST,
  ARTICLES_CREATE,
} from "../constants/articlesConstants";

export const articlesListReducer = (state = [], action) => {
  switch (action.type) {
    case ARTICLES_LIST:
      return action.payload;
    case ARTICLES_DETAILS:
      return action.payload;
    case ARTICLES_CREATE:
      return [...state, action.payload];
    default:
      return state;
  }
};
