import { USER_CREATE, USER_LOGIN } from "../constants/userConstants";

export const UserReducers = (user = [], action) => {
  switch (action.type) {
    case USER_CREATE:
      return [...user, ...action.payload];
    case USER_LOGIN:
      return [...user, ...action.payload];
    default:
      return user;
  }
};
