import {
  ARTICLES_DETAILS,
  ARTICLES_LIST,
} from "../constants/articlesConstants";
import * as api from "../api/articles-api";

export const listArticles = () => async (dispatch) => {
  const { data } = await api.fetchArticles();
  try {
    dispatch({
      type: ARTICLES_LIST,
      payload: data,
    });
  } catch (error) {
    console.log(error.message);
  }
};
export const listArticlesDetails = (id) => async (dispatch) => {
  try {
    const { data } = await api.fetchArticlesDetails(id);
    dispatch({
      type: ARTICLES_DETAILS,
      payload: data,
    });
  } catch (error) {
    console.log(error.message);
  }
};
