import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import SubArticles from "../components/subArticles";

const SubArticlesScreen = () => {
  const articlesList = useSelector((state) => state.articlesList);
  const subarticlesList = useSelector((state) => state.subarticlesList);

  return (
    <div>
        {subarticlesList.map((subtitle) => (
        //   <button onClick={() => console.log(subtitle)}>Click</button>
          <SubArticles key={subtitle.slug} subtitle={subtitle} />
        ))}
    </div>
  );
};

export default SubArticlesScreen;
