import {
    SUBARTICLES_LIST
  } from "../constants/subarticlesConstants";
  import * as api from "../api/articles-api";

  export const listArticles = () => async (dispatch) => {
    const { data } = await api.fetchSubarticles();
    try {
      dispatch({
        type: SUBARTICLES_LIST,
        payload: data,
      });
    } catch (error) {
      console.log(error.message);
    }
  };
