import {
    SUBARTICLES_LIST
  } from "../constants/subarticlesConstants";

  export const subarticlesListReducer = (state = [], action) => {
    switch (action.type) {
      case SUBARTICLES_LIST:
        return action.payload;
      default:
        return state;
    }
  };
