import React, { useState, useEffect } from "react";
import { useHistory, useParams } from "react-router-dom";
import { useDispatch } from "react-redux";
import {
  createSubArticle,
  listSubArticles,
  listSubArticlesDetails,
} from "../actions/subarticles-actions";
import { Form, Button } from "react-bootstrap";
import { useSelector } from "react-redux";
import { listArticles } from "../actions/articles-action";
import FormData from "form-data";

const SubArticleEdit = () => {
  let history = useHistory();
  const { slug } = useParams();
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(listArticles());
    dispatch(listSubArticlesDetails(slug));
  }, [dispatch]);
  const { articles } = useSelector((state) => state?.articlesList);
  // if (slug) {
  //   const { subarticle, loading } = useSelector(
  //     (state) => state.subarticlesDetails
  //   );
  // }

  const [product, setProduct] = useState({
    title: "",
    description: "",
    image: "",
    available_on: "",
    maintitle: [],
